var video = document.querySelector("#video");
var canvas1 = document.querySelector("#canvas1");
var canvas2 = document.querySelector("#canvas2");

const getColor = (txt) => {
  switch (txt) {
    case "0":
      return "#32CD32";
    default:
      break;
  }
};

function beep() {
  var snd = new Audio(
    "data:audio/wav;base64,"
  );
  snd.play();
}

const drawRect = (detections, ctx) => {
  // Loop through each prediction
  ctx.clearRect(0, 0, 640, 480);
  detections.forEach((prediction) => {
    // Extract boxes and classes
    const {
      bbox: { x, y, width, height },
      class: text,
      confidence,
      color,
    } = prediction;
    console.log(text);
    const acc = Math.round(confidence * 100);
    ctx.strokeStyle = text == "0" ? "#3fff00" : color;
    ctx.fillStyle = text == "0" ? "#3fff00" : color;
    if (text == "0" && width > 400) {
      beep();
      ctx.strokeStyle = text == "0" ? "#ff4000" : color;
      ctx.fillStyle = text == "0" ? "#ff4000" : color;
    }
    ctx.font = "2em Monospace";

    // Draw rectangles and text
    ctx.beginPath();
    ctx.fillText(
      `${text == "0" ? "person" : text} ${width.toFixed(0)}`,
      x - width / 2 + 5,
      y - height / 2 + 24
    );
    ctx.lineWidth = 3;
    ctx.rect(x - width / 2, y - height / 2, width, height);
    ctx.stroke();
  });
};

if (navigator.mediaDevices.getUserMedia) {
  navigator.mediaDevices
    .getUserMedia({ video: { width: 640, height: 480 } })
    .then(function (stream) {
      video.srcObject = stream;
      roboflow
        .auth({
          publishable_key: "rf_5w20VzQObTXjJhTjq6kad9ubrm33",
        })
        .load({
          model: "trees-88tmr",
          version: 3,
        })
        .then(function (model) {
          setInterval(() => {
            model.detect(video).then(function (predictions) {
              const ctx = canvas1.getContext("2d");
              drawRect(predictions, ctx);
            });
          }, 500);
        });
      roboflow
        .auth({
          publishable_key: "rf_5w20VzQObTXjJhTjq6kad9ubrm33",
        })
        .load({
          model: "human-counter",
          version: 2,
        })
        .then(function (model) {
          setInterval(() => {
            model.detect(video).then(function (predictions) {
              const ctx = canvas2.getContext("2d");
              drawRect(predictions, ctx);
            });
          }, 500);
        });
    })
    .catch(function (error) {
      console.error(error);
      console.log("Something went wrong!");
    });
}
